<?php
    error_reporting(E_ERROR | E_PARSE); // Disable error
    $isSuccess = false;
    $message = "Please login to continue.";
    
    if (isset($_COOKIE["userInfo"])) {
        // Prep
        $cookie = explode("---",$_COOKIE["userInfo"]);
        $username = $cookie[0];
        $pass = $cookie[1];
        $conn = mysqli_connect("localhost","<username>","<password>","<database_name>"); // Add your MySQL database info.
        $sql_cmd = "select * from account_list where username='" .$username. "'";
        // End prep
        $statement = $conn->query($sql_cmd);
        $row = $statement->fetch_assoc();
        if ($username == $row["username"] && $pass == $row["password"]) {$isSuccess = true;}
        else {$message = "Invalid cookie data. Please login again.";};
        $conn->close();
    } else if ($_POST["username"] != "" && $_POST["password"] != "") {
        // Prep
        $username = $_POST["username"];
        $pass = hash("sha384",$_POST["password"]);
        $sql_cmd = "select * from <table_name> where username='" .$username. "'"; // Add your table name.
        // End prep
        $statement = $conn->query($sql_cmd);
        if ($statement->num_rows > 0) {
            $row = $statement->fetch_assoc();
            if ($username == $row["username"] && $pass == $row["password"]) {
                $isSuccess = true;
                setcookie("userInfo",implode("---",[$username,$pass]),time() + (86400 * 30),"/");
            } else {$message = "Invalid password. Try again.";};
        } else {$message = "User didn't exist in the database.";};
        $conn->close();
    };
    if ($isSuccess == true) {header("Location: /");exit;}; // Redirect to / if account is correct. 
?>
